changeSet.sql:
DROP TRIGGER IF EXISTS `stockmgmt_location_after_insert`;
$$
CREATE TRIGGER `stockmgmt_location_after_insert` AFTER INSERT ON `location_tag_map`
  FOR EACH ROW
   If Not Exists (select party_id from stockmgmt_party where location_id = NEW.location_id) THEN
    BEGIN
      IF NEW.location_tag_id IN ( select location_tag_id from location_tag where uuid in ('89a80c4d-2899-11ed-bdcb-507b9dea1806','fe7c970f-2aba-11ed-ba4a-507b9dea1806','e539ebca-2899-11ed-bdcb-507b9dea1806')) THEN
        INSERT INTO stockmgmt_location_tree (parent_location_id, child_location_id, depth, uuid)
        VALUES (NEW.location_id, NEW.location_id, 0, uuid());

        INSERT INTO stockmgmt_party(location_id, creator, date_created, uuid)
          VALUES(NEW.location_id, 1, CURDATE(), uuid());
      END IF;
      END;
    END IF;
$$
